import React, { Component } from 'react';
import Search from './Search'
import styles from '../styles/List.css';
import axios from 'axios';

import { BrowserRouter as Router, Route, Link } from 'react-router-dom';

class List extends Component {

 

  componentDidMount(){
    axios.get('https://api.myjson.com/bins/tf31e')
    .then(json => json.data.Search.map(result => (
      {
        
        cover: `${result.Poster}`,
        name: `${result.Title}`,
        year: `${result.Year}`,
      
      })))
    .then(newData => this.setState({books: newData, store: newData}))
    .catch(error => alert(error))
  }

  filterNames(e){
    this.setState({books: this.state.store.filter((item) => item.name.toLowerCase().includes(e.target.value.toLowerCase()))})
  }


 handleClick = () => {
    function clickable(){
      var classname = document.getElementsByClassName("class-static");
      var myFunction = function() {
        var cover = this.getAttribute("data-urlcover"); 
        alert(cover);              
      };
      Array.from(classname).forEach(function(element) {
        element.addEventListener("click", myFunction);
      });
      
    }

    function callBtn(){
        document.getElementById("myBtn").addEventListener("click", function(){
        var element = document.getElementById("root");
        element.classList.toggle("active");
        // element.style.backgroundColor  = element.style.backgroundColor === 'red' ? 'blue' : 'red';
      });
     }

     function toggleMenu(){
        // document.querySelector('.menu').addEventListener("click", function(){
        // var menu = document.querySelector('.menu');
        // menu.classList.toggle('active');
        //});

        var activeLink = document.querySelectorAll('.menus');

        for (var i = 0; i < activeLink.length; i++) {
            activeLink[i].addEventListener('click', function(event) {
              for (var j = 0; j < activeLink.length; j++) {
                activeLink[j].classList.remove("active-menu");
            }
            
             this.classList.add('active-menu'); 
             
            });
        }

     
     }   
    setTimeout(function(){ clickable(); callBtn(); toggleMenu(); }, 900);
  }

  

  constructor(props){
    super(props);
    this.handleLoad = this.handleLoad.bind(this);
    this.state = {
      books: [],
      store: []
    }
  }

  componentDidMount() {
    window.addEventListener('load', this.handleLoad);
  }

  handleLoad() {
    this.handleClick();
  }

 
  render() {
    const {books} = this.state
    return (
      <div>
        <Search searchFunc={(e) => this.filterNames(e)}/>
        <div className={styles['list']}>

           
          <section className={styles['cards-wrap']}>
          {books.map(
            book =>
            <div className={`${styles['card']} class-static`} data-urlcover={book.cover} >
              <div className={styles['card-image']}>
                <img src={book.cover} />
              </div>
              <div class={styles['product-info']}>
                <h5>{book.name}</h5>
                <h6>{book.year}</h6>
              </div>          
             </div>
            )}
            </section>
        </div>
      </div>
    );
  }
}

export default List;
